(function($){
    $.fn.cond = function() {
        var e, // Variable no definida, parece ser un valor "undefined" para comparación
            a = arguments, // Array de argumentos pasados a la función
            b = 0, // Contador para iterar a través de los argumentos
            f, // Condición actual
            d, // Función de resultado o valor correspondiente a la condición
            c; // Resultado final

        // Bucle para iterar a través de las condiciones y resultados
        while (!f && b < a.length) {
            f = a[b++]; // Obtener la condición actual y aumentar el contador
            d = a[b++]; // Obtener el resultado o función correspondiente
            f = $.isFunction(f) ? f.call(this) : f; // Evaluar la condición si es una función
            c = !d ? f : f ? d.call(this, f) : e; // Evaluar el resultado si la condición es verdadera
        }

        // Devolver el resultado si es definido, de lo contrario, el contexto jQuery
        return c !== e ? c : this;
    };
})(jQuery);